sci:arg.sci:value:
if property(@Label) is defined then
	@label_ := property(@Label);
elsif substring(@Label, 1, 1) eq '@' and globalproperty(strReplace(@Label, '@', '')) is defined then
	@label_ := globalproperty(strReplace(@Label, '@', ''));
else
	@label_ := strReplace(strReplace(@Label, "'", ''), '"', '');
end if;

#displayName := trim(Parameter('Expression', 2));
if(strlength(#displayName) > 0) then	
	/* Find the first line break */
	#nl := FindSubstring(#displayName, "\r");
	if #nl <= 0 then
		#nl := FindSubstring(#displayName, "\n");
	end if;

	/* Keep the first line only */
	if (#nl > 0) then
		StrErase(#displayName, #nl);
	end if;
	
	#displayName;
else
	"";
end if;

