sci:arg.sci:enabledscript:
prefixLength := StringLen('Link');
fullLength := StringLen(ParameterName());
prefix := SubString(ParameterName(), 1, fullLength - prefixLength); 
protocolLinkParameterName := prefix.'Protocol Link';
enabled := true;
if (parameter(protocolLinkParameterName) is Defined ) then 
	enabled := Parameter(protocolLinkParameterName) eq '';
	if (not enabled) then
		 SetDisabledDetails('This parameter is enabled only when Protocol Link is not set');
	End if;
end if;	
enabled;

prefixLength := StringLen('Protocol Link');
fullLength := StringLen(ParameterName());
prefix := SubString(ParameterName(), 1, fullLength - prefixLength); 
linkParameterName := prefix.'Link';
enabled := true;
if (parameter(linkParameterName) is Defined ) then 
	enabled := Parameter(linkParameterName) eq '';
	if (not enabled) then
		 SetDisabledDetails('This parameter is enabled only when Link is not set');
	End if;
End if;
enabled;

