sci:arg.sci:value:
if @'Label Location' is defined and @'Label Location' eq 'Left of Field' then
	@inline := 'inline';
else
	@inline := '';
end if;

if @label is defined and @label ne '' then
	@thislabel := '<label>$(label)</label>';
else
	@thislabel := '';
end if;

#displayName := trim(Parameter('Expression', 2));
if(strlength(#displayName) > 0) then	
	/* Find the first line break */
	#nl := FindSubstring(#displayName, "\r");
	if #nl <= 0 then
		#nl := FindSubstring(#displayName, "\n");
	end if;

	/* Keep the first line only */
	if (#nl > 0) then
		StrErase(#displayName, #nl);
	end if;
	
	#displayName;
else
	"";
end if;

if @Type is defined and @Type eq 'Multiple Files' then
	@seltype_ := 'multiple';
else
	@seltype_ := '';
end if;

