sci:arg.sci:value:
IF @'/scitegic/report/HtmlReportIndex' IS NOT DEFINED THEN
	/* Make /scitegic node, if needed */
	IF (NodeHasChild(GlobalRoot(), 'scitegic')) THEN
		#scitegicNode := NodeGetFirstChild(GlobalRoot(), 'scitegic');
	ELSE
		#scitegicNode := NodeAppendChild(GlobalRoot());
		NodeSetType(#scitegicNode, 'scitegic');
	END IF;
	/* Make /scitegic/report node, if needed */
	IF (NodeHasChild(#scitegicNode, 'report')) THEN
		#reportNode := NodeGetFirstChild(#scitegicNode, 'report');
	ELSE
		#reportNode := NodeAppendChild(#scitegicNode);
		NodeSetType(#reportNode, 'report');
	END IF;
	/* Set the initial property value */
	NodeProperty(#reportNode, 'HtmlReportIndex') := 1;
	#fileNumber := '';
ELSE
	@'/scitegic/report/HtmlReportIndex'++;
	#fileNumber := @'/scitegic/report/HtmlReportIndex';
END IF;

IF @Title != '' THEN
	/* Replace invalid filename characters in title with underscores (_) */
	#Title := RSUBST(@Title, '[:*"/\\<>|\?#%]', "_", "g");
	/* Change all whitespace characters in title to single space ( ) */
	#Title := RSUBST(#Title, '[\s]', " ", "g");
	/* Strip any leading whitespace */
	#Title := RSUBST(#Title, '^\s+', '');
ELSE
	#Title := 'HTML Report';
	/* Include number even for report 1 */
	#fileNumber := @'/scitegic/report/HtmlReportIndex';
END IF;

@ServerFilePath := @JobDir . '/' . #Title . #fileNumber . '.html';
@ServerFilePathForwardSlashes := strReplace(@ServerFilePath, '\', '/');
@ServerFilePathZIP := @JobDir . '/' . #Title . #fileNumber . '.zip';

