sci:arg.sci:enabledscript:
#parallel := Parameter('Parallel Processing Options');
#rtcForEachCategory := (Parameter('RunToCompletion') eq 'True - For Each Category');
if (not #parallel) then
	SetDisabledDetails("This parameter is only applicable if 'Parallel Processing Options' is 'True'");
elsif (#rtcForEachCategory) then
	SetDisabledDetails("This parameter is not applicable when 'RunToCompletion' is set to 'True - For Each Category'");
end if;
#parallel and not #rtcForEachCategory;

sci:arg.sci:validscript:
/* Server Processes must have the same number of comma separated entries as the Server parameter and must have a value*/
#processes := parameter();
expand(',', #processes);
#servers := parameter('Parallel Processing Options Server');
expand(',', #servers);

if (numvalues(#processes) == 0) then
	SetInvalidDetails('This parameter is required.');
else
	SetInvalidDetails('The number of server processes (' . numvalues(#processes) . ') must equal the number of servers (' . numvalues(#servers) . ').');
end if;
numvalues(#processes) > 0 and numvalues(#processes) == numvalues(#servers);

sci:arg.sci:legalvalscript:
if (Parameter('Required')) then
		resize(lvals, 1);
	    lvals[1] := True;
	else
		resize(lvals, 2);
	    lvals[1] := True; lvals[2] := False;
	end if;
	SetLegalValues(lvals);

