sci:arg.sci:enabledscript:
#parallel := Parameter('Parallel Processing Options');
#rtcForEachCategory := (Parameter('RunToCompletion') eq 'True - For Each Category');
if (not #parallel) then
	SetDisabledDetails("This parameter is only applicable if 'Parallel Processing Options' is 'True'");
elsif (#rtcForEachCategory) then
	SetDisabledDetails("This parameter is not applicable when 'RunToCompletion' is set to 'True - For Each Category'");
end if;
#parallel and not #rtcForEachCategory;

sci:arg.sci:validscript:
/* Server Processes must have the same number of comma separated entries as the Server parameter and must have a value*/
#processes := parameter();
expand(',', #processes);
#servers := parameter('Parallel Processing Options Server');
expand(',', #servers);

if (numvalues(#processes) == 0) then
	SetInvalidDetails('This parameter is required.');
else
	SetInvalidDetails('The number of server processes (' . numvalues(#processes) . ') must equal the number of servers (' . numvalues(#servers) . ').');
end if;
numvalues(#processes) > 0 and numvalues(#processes) == numvalues(#servers);

regressionLineParameterName := StrReplace(ParameterName(), "Regression Line Expression", "Regression Line");
valid := Parameter(regressionLineParameterName) ne 'Custom' OR Parameter() ne '';
if (not valid) then
     SetInvalidDetails('This parameter is required when Regression Line is set to "Custom"');
End if;
valid;

sci:arg.sci:enabledscript:
regressionLineParameterName := StrReplace(ParameterName(), "Regression Line Expression", "Regression Line");
enabled := Parameter(regressionLineParameterName) eq 'Custom';
if (not enabled) then
     SetDisabledDetails('This parameter is enabled only when Regression Line is set to "Custom"');
End if;
enabled;

protocolLinkParameterName := StrReplace(ParameterName(), "Actions Link", "Actions Protocol Link");
enabled := Parameter(protocolLinkParameterName) eq '';
if (not enabled) then
     SetDisabledDetails('This parameter is enabled only when Protocol Link is not set');
End if;
enabled;

linkParameterName := StrReplace(ParameterName(), "Protocol Link", "Link");
enabled := Parameter(linkParameterName) eq '';
if (not enabled) then
     SetDisabledDetails('This parameter is enabled only when Link is not set');
End if;
enabled;

prefixLength := StringLen('Link');
fullLength := StringLen(ParameterName());
prefix := SubString(ParameterName(), 1, fullLength - prefixLength); 
protocolLinkParameterName := prefix.'Protocol Link';
enabled := true;
if (parameter(protocolLinkParameterName) is Defined ) then 
	enabled := Parameter(protocolLinkParameterName) eq '';
	if (not enabled) then
		 SetDisabledDetails('This parameter is enabled only when Protocol Link is not set');
	End if;
end if;	
enabled;

prefixLength := StringLen('Protocol Link');
fullLength := StringLen(ParameterName());
prefix := SubString(ParameterName(), 1, fullLength - prefixLength); 
linkParameterName := prefix.'Link';
enabled := true;
if (parameter(linkParameterName) is Defined ) then 
	enabled := Parameter(linkParameterName) eq '';
	if (not enabled) then
		 SetDisabledDetails('This parameter is enabled only when Link is not set');
	End if;
End if;
enabled;

ModelParameterName := StrReplace(ParameterName(), "Subscriber Event Handling", "Publisher Subscriber Model");
enabled := Parameter(ModelParameterName) not EQ 'Publisher' AND Parameter(ModelParameterName) not EQ 'None';
if (not enabled) then
     SetDisabledDetails('This parameter is not enabled when the Publisher Subscriber Model is set to "Publisher" only or "None"');
End if;
enabled;

sci:arg.sci:validscript:
maxParameterName := StrReplace(ParameterName(), "Minimum", "Maximum");
valid := true;
minValue := Parameter();
if (minValue ne '' and IsNumber(minValue)) then
	maxValue := Parameter(maxParameterName);
	if (maxValue ne '' and IsNumber(maxValue)) then 
		valid := maxValue > minValue;
		invalidMessage := 'Minimum should be less than Maximum';
	end if;
	
	if (valid) then 
		scalingParameterName := StrReplace(ParameterName(), "Minimum", "Scaling");
		if Parameter(scalingParameterName) Is Defined and FindSubstring(Parameter(scalingParameterName), 'Logarithmic') > 0 then
				valid := minValue > 0;
				invalidMessage := 'This parameter should be greater than 0 for logarithmic scaling';
		end if;			
	end if;
end if;	
	
if (not valid) then 
	SetInvalidDetails(invalidMessage);
end if;

valid;

minParameterName := StrReplace(ParameterName(), "Maximum", "Minimum");
valid := true;
maxValue := Parameter();
if (maxValue ne '' and IsNumber(maxValue)) then
	minValue := Parameter(minParameterName);
	if(minValue ne '' and IsNumber(minValue)) then 
		valid := minValue < maxValue;
		invalidMessage := 'Minimum should be less than Maximum';
	end if;

	if (valid) then 
		scalingParameterName := StrReplace(ParameterName(), "Maximum", "Scaling");
		if Parameter(scalingParameterName) Is Defined and FindSubstring(Parameter(scalingParameterName), 'Logarithmic') > 0 then
				valid := maxValue > 0;
				invalidMessage := 'This parameter should be greater than 0 for logarithmic scaling';
		end if;		
	end if;	
end if;	
	
if (not valid) then 
		SetInvalidDetails(invalidMessage);
end if;

valid;

sci:arg.sci:enabledscript:
ValuesTypeParameterName := StrReplace(ParameterName(), "Tickmark Label Format", "Tickmark Label Type");
enabled := Parameter(ValuesTypeParameterName) NE 'Based on Input Data';
if (not enabled) then
     SetDisabledDetails('This parameter is not enabled when Label Type is set to "Based on Input Data"');
End if;
enabled;

