sci:arg.sci:enabledscript:
enabled := Parameter("Include Live Molecule") EQ 'True';
IF (NOT enabled) THEN
     SetDisabledDetails('This parameter is enabled only when Include Live Molecule is set to True.');
END IF;
enabled;

enabled := Parameter("Include Live Molecule") EQ 'False';
IF (NOT enabled) THEN
     SetDisabledDetails('This parameter is enabled only when Include Live Molecule is set to False.');
END IF;
enabled;

